  function handleID(id) {
    const selectedWallet = walletData.find((wallet) => wallet.id === id);
    if (selectedWallet) setWalletArray([selectedWallet]);
  }

  return (
    <WalletContext.Provider
      value={{
        walletVisible,
        initVisible,
        formVisible,
        walletArray,
        setWalletVisible,
        setInitVisible,
        setFormVisible,
        handleID,
        walletData,
        success,
        setSuccess,
      }}
    >
      {children}
    </WalletContext.Provider>
  );
